import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import puzzleImage from "./puzzle.jpg";
import kissImage from "./kiss.jpg";
import girlfriendImage from "./girlfriend.jpg";
import runnerSprite from "./runner.png";
import "./App.css";

const App = () => {
  const [page, setPage] = useState("screen1");
  const [debClicks, setDebClicks] = useState(0);
  const [runnerPosition, setRunnerPosition] = useState(0);
  const checkpoints = [
    { position: 20, object: "ðŸ“š", action: () => setPage("miniGame1") },
    { position: 40, object: "ðŸ“±", action: () => setPage("miniGame2") },
    { position: 60, object: "ðŸ§©", action: () => setPage("miniGame3") },
    { position: 80, object: "ðŸ’Œ", action: () => setPage("miniGame4") }
  ];

    useEffect(() => {
  if (page.startsWith("screen")) {
    const interval = setInterval(() => {
      setRunnerPosition((pos) => {
        const nextPos = pos + 0.5; // adjust speed

        // trigger checkpoint actions
        checkpoints.forEach((cp) => {
          if (pos < cp.position && nextPos >= cp.position) {
            cp.action();
          }
        });

        return nextPos >= 100 ? 100 : nextPos; // stop at end
      });
    }, 20);

    return () => clearInterval(interval);
  }
}, [page]);


  // useEffect(() => {
  //   if (page.startsWith("screen")) {
  //     const interval = setInterval(() => {
  //       setRunnerPosition((pos) => {
  //         if (pos >= 90) {
  //           clearInterval(interval);
  //           switch (page) {
  //             case "screen1": setPage("miniGame1"); break;
  //             case "screen2": setPage("miniGame2"); break;
  //             case "screen3": setPage("miniGame3"); break;
  //             case "screen4": setPage("miniGame4"); break;
  //             case "screen5": setPage("miniGame5"); break;
  //             case "screen6": setPage("miniGame6"); break;
  //             case "screen7": setPage("miniGame7"); break;
  //             default: break;
  //           }
  //         }
  //         return pos + 0.5;
  //       });
  //     }, 20);
  //     return () => clearInterval(interval);
  //   }
  // }, [page]);

  const handleDebClick = () => setDebClicks((prev) => prev + 1);

  const renderPage = () => {
    switch (page) {
      case "screen1": case "screen2": case "screen3": case "screen4":
      case "screen5": case "screen6": case "screen7":
    return (
    <div className="screen pastel" style={{ position: "relative" }}>
      <motion.img
        src={runnerSprite}
        alt="runner"
        style={{ width: "150px", height: "150px", position: "absolute", top: "50%", transform: "translateY(-50%)" }}
        className="runner"
        initial={{ x: 0 }} // Start from the left
        animate={{ x: `${runnerPosition}%` }}
        transition={{ type: "linear", duration: 0.02 }}
      />

      {/* Visible objects */}
      {checkpoints.map((cp, idx) => (
        <div
          key={idx}
          style={{
            position: "absolute",
            left: `${cp.position}%`,
            top: "50%", // adjust vertical position
            transform: "translate(-50%, -50%)",
            fontSize: "2rem",
            pointerEvents: "none"
          }}
        >
          {cp.object}
        </div>
      ))}

      <h1>Runner Game</h1>
      <p>Keep running and encounter objects!</p>
    </div>
  );


      case "miniGame1":
        return (
          <div className="screen pastel">
            <h1>4th December</h1>
            <p>It's 7am, you have a test at school what do you do?</p>
            <button onClick={() => setPage("page2_fail")}>a) Sleep</button>
            <button onClick={() => setPage("page2_fail")}>b) Study</button>
            <button onClick={() => setPage("page2_success")}>c) Get on userphone</button>
          </div>
        );
      case "page2_fail":
        return (
          <div className="screen pastel">
            <p>Babe, no. That's lame :/</p>
            <button onClick={() => setPage("miniGame1")}>Try Again</button>
          </div>
        );
      case "page2_success":
        return (
          <div className="screen pastel">
            <p>BINGO! That's my boy :3</p>
            <button onClick={() => { setRunnerPosition(0); setPage("screen2"); }}>Continue Running</button>
          </div>
        );

      case "miniGame2":
        return (
          <div className="screen pastel">
            <h1>14th December</h1>
            <p>A potential predator wants to know your location</p>
            <button onClick={() => { setRunnerPosition(0); setPage("screen3"); }}>a) GIVE YOUR ADDRESS</button>
            <button onClick={() => { setRunnerPosition(0); setPage("screen3"); }}>b) GIVE YOUR ADDRESS</button>
            <button onClick={() => { setRunnerPosition(0); setPage("screen3"); }}>c) GIVE YOUR ADDRESS</button>
          </div>
        );

      case "miniGame3":
        return (
          <div className="screen pastel">
            <h1>15th December</h1>
            <p>Solve the 3x3 puzzle:</p>
            <Puzzle onSolved={() => setPage("puzzleSuccess")} />
          </div>
        );
      case "puzzleSuccess":
        return (
          <div className="screen black">
            <div className="notification">
              <h2>Thank you for signing up to love!</h2>
              <p>
                Any attempt to revoke your subscription will have serious consequences.
                You will now have a lifetime supply of Samara, and associated parts and duties.
              </p>
              <button onClick={() => { setRunnerPosition(0); setPage("quiz1"); }}>Continue</button>
            </div>
          </div>
        );

      case "quiz1":
        return (
          <div className="screen pastel">
            <p>Q1: Did you say this: I WANNA GO TO INDIA SM ALREADY</p>
            <button onClick={() => setPage("quiz1_success")}>a) Yes</button>
            <button onClick={() => alert("Try again")}>b) Couldn't have been me</button>
            <button onClick={() => alert("Try again")}>c) maybe under gunpoint</button>
          </div>
        );
      case "quiz1_success":
        return (
          <div className="screen pastel">
            <p>Q2: Who said ily first</p>
            <button onClick={() => setPage("quiz3")}>a) me</button>
            <button onClick={() => setPage("quiz3")}>b) you</button>
            <button onClick={() => setPage("quiz3")}>c) we</button>
            <button onClick={() => setPage("quiz3")}>d) idk</button>
          </div>
        );
      case "quiz3":
        return (
          <div className="screen pastel">
            <p>Q3: do u hate me :pleading_face:</p>
            <button onClick={() => alert("Try again")}>a) yes</button>
            <button onClick={() => setPage("page6")}>c) what is hate</button>
          </div>
        );

      case "page6":
        return (
          <div className="screen pastel">
            <p>My baby's so good at this, here have a kiss</p>
            <img src={kissImage} alt="kiss" />
            <button onClick={() => { setRunnerPosition(0); setPage("screen4"); }}>Continue Running</button>
          </div>
        );

      case "miniGame4":
        return (
          <div className="screen pastel">
            <h1>23rd January</h1>
            <p>This girl wants to be your girlfriend. What do you do?</p>
            <img src={girlfriendImage} alt="girlfriend" />
            <button onClick={() => { setRunnerPosition(0); setPage("screen5"); }}>a) say yes</button>
            <button onClick={() => { setRunnerPosition(0); setPage("screen5"); }}>b) say yes</button>
            <button onClick={() => { setRunnerPosition(0); setPage("screen5"); }}>c) say yes</button>
          </div>
        );

      case "miniGame5":
        case "miniGame5":
          return <DebGame onComplete={() => { setRunnerPosition(0); setPage("screen6"); }} />;


      case "miniGame6":
        return (
          <div className="screen pastel">
            <h1>Your psycho discord girlfriend sent auditory hieroglyphs at 3AM</h1>
            <button onClick={() => alert("Smart boy, think again :))")}>a) RUN</button>
            <button onClick={() => { setRunnerPosition(0); setPage("screen7"); }}>b) wait</button>
            <button onClick={() => { alert("tch tch tch, typical."); setRunnerPosition(0); setPage("screen7"); }}>c) feed into her insanity, say that you love her</button>
          </div>
        );

      case "miniGame7":
        return (
          <div className="screen pastel">
            <h1>
              In an experiment to test the affinity of an isolated species - henceforth addressed to as "Specimen-1", we (the universe and fate and shit) introduce Specimen-2 to her habitat. Specimen-1 starts showing varied response to this interaction. To confirm the effect of Specimen-2 on Specimen-1, we remove Specimen-2.

              Specimen-1 shows withdrawal symptoms. What should be our next step?
            </h1>
            <button onClick={() => alert("This is a non-cancellable program, try again")}>a) Terminate the experiment</button>
            <button onClick={() => setPage("page10")}>b) Wait and watch symptoms</button>
            <button onClick={() => setPage("page11")}>c) Bring back Specimen-2</button>
          </div>
        );

      case "page10":
        return (
          <div className="screen pastel">
            <h1>GAME OVER</h1>
            <button onClick={() => { setRunnerPosition(0); setPage("screen1"); }}>Restart</button>
          </div>
        );

      case "page11":
        return (
          <div className="screen pastel">
            <h1>15th September</h1>
            <p>Your personalised message and images go here!</p>
          </div>
        );

      default:
        return <div>Loading...</div>;
    }
  };

  return (
    <AnimatePresence mode="wait">
      <motion.div key={page} initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }}>
        {renderPage()}
      </motion.div>
    </AnimatePresence>
  );
};


// -------- Puzzle Component --------
const Puzzle = ({ onSolved }) => {
  const pieceSize = 100; // size of each puzzle piece
  const gridSize = 3; // 3x3 puzzle
  const fullImageSize = pieceSize * gridSize;

  const [pieces, setPieces] = useState(
    Array.from({ length: 9 }, (_, i) => i).sort(() => Math.random() - 0.5)
  );

  const handleDragStart = (e, idx) => {
    e.dataTransfer.setData("dragIdx", idx);
  };

  const handleDrop = (e, idx) => {
    e.preventDefault();
    const dragIdx = Number(e.dataTransfer.getData("dragIdx"));
    if (dragIdx === undefined) return;

    const newPieces = [...pieces];
    [newPieces[idx], newPieces[dragIdx]] = [newPieces[dragIdx], newPieces[idx]];
    setPieces(newPieces);

    if (newPieces.every((val, i) => val === i)) {
      onSolved();
    }
  };

  return (
    <div
      className="puzzle-grid"
      style={{
        display: "grid",
        gridTemplateColumns: `repeat(${gridSize}, ${pieceSize}px)`,
        gridTemplateRows: `repeat(${gridSize}, ${pieceSize}px)`,
        gap: "2px",
      }}
    >
      {pieces.map((val, idx) => (
        <div
          key={idx}
          className="puzzle-piece"
          style={{
            width: pieceSize,
            height: pieceSize,
            overflow: "hidden",
            cursor: "grab",
          }}
          draggable
          onDragStart={(e) => handleDragStart(e, idx)}
          onDrop={(e) => handleDrop(e, idx)}
          onDragOver={(e) => e.preventDefault()}
        >
          <img
            src={puzzleImage}
            alt="puzzle"
            style={{
              width: `${fullImageSize}px`,
              height: `${fullImageSize}px`,
              objectFit: "cover",
              objectPosition: `-${(val % gridSize) * pieceSize}px -${Math.floor(
                val / gridSize
              ) * pieceSize}px`,
              pointerEvents: "none",
            }}
          />
        </div>
      ))}
    </div>
  );
};


//----------DebGame component--------------

const DebGame = ({ onComplete }) => {
  const [debs, setDebs] = useState([]);

  useEffect(() => {
    // Function to spawn a new DEB
    const spawnDeb = () => {
      setDebs((prev) => [
        ...prev,
        {
          id: Date.now() + Math.random(),
          top: Math.random() * 80 + 10,
          left: Math.random() * 80 + 10,
        },
      ]);
    };

    // Spawn DEBs continuously every 0.5s
    const interval = setInterval(spawnDeb, 500);

    // Stop spawning after 5 seconds
    const timeout = setTimeout(() => clearInterval(interval), 5000);

    return () => {
      clearInterval(interval);
      clearTimeout(timeout);
    };
  }, []);

  const handleClick = (id) => {
    setDebs((prev) => {
      const newDebs = prev.filter((deb) => deb.id !== id);
      if (newDebs.length === 0) {
        onComplete(); // Game ends when all DEBs are gone
      }
      return newDebs;
    });
  };

  return (
    <div className="screen pastel" style={{ position: "relative", height: "100vh" }}>
      <h1 style={{ color: "red" }}>A malicious, jealous, havoc wreaking DEB just appeared!</h1>
      <p>Click DEB words to eliminate her!</p>
      {debs.map((deb) => (
        <span
          key={deb.id}
          onClick={() => handleClick(deb.id)}
          style={{
            position: "absolute",
            top: `${deb.top}%`,
            left: `${deb.left}%`,
            color: "red",
            cursor: "pointer",
            fontWeight: "bold",
            userSelect: "none",
            fontSize: "20px",
          }}
        >
          DEB
        </span>
      ))}
      {debs.length === 0 && (
        <button
          style={{ position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)" }}
          onClick={onComplete}
        >
          Continue
        </button>
      )}
    </div>
  );
};





export default App;
